select s.student_id , s.student_name , a.subject_name ,coalesce(count(e.student_id), 0) attended_exams from Students s join Subjects a left join Examinations e on s.student_id = e.student_id and a.subject_name = e.subject_name group by s.student_id, s.student_name, a.subject_name order by s.student_id,a.subject_name